import React from 'react';

const testimonials = [
  {
    quote: "Excited to join TruRide as a driver! Fair earnings, community support, and perks like insurance and ad-based extra income make it a game-changer. Can't wait for the launch! ðŸš—ðŸ’¼",
    imageSrc: "assets//testimonial/auto.png"
  },
  {
    quote: "Can't wait to try out TruRide as a rider! The user-friendly platform and reward points for every ride, combined with unique AI route technology, make it the ride-hailing solution I've been searching for. ðŸš—ðŸŽ‰",
    imageSrc: "assets/testimonial/cab.png"
  },
  {
    quote: "Exciting times ahead with TruRide! With a driver's union, community benefits, and a platform designed for both drivers and riders, we're thrilled to join this integrated mobility network. ðŸŒŸðŸš—",
    imageSrc: "assets/testimonial/autobook.png"
  }
];

export default function Testimonials() {
  return (
    <div className='mt-10'>
      <section className='mt-40 max-w-full h-auto bg-[#f9f9f9] p-5'>
        <div className="flex flex-col items-center">
          <h3 className='text-center mt-5 text-[#100062] text-[40px] font-bold'> 
            Our Drivers & Riders <strong>Testimonials</strong>
          </h3>
          <div className='mt-20 flex justify-center'>
            <img 
              src="assets/testimonial/line2.png" 
              alt="" 
              className="rotate-90 w-[30px] mt-25" 
              style={{ alignItems: "center", marginLeft: "-350px" }} 
            />
          </div>
        </div>

        {testimonials.map((testimonial, index) => (
          <div key={index} className='flex flex-col items-center justify-center mt-25 mb-20'>
            <div className='flex justify-center mt-25'>
              <img src="assets/testimonial/auto.png" alt="" className='w-[668px] h-[236px]' />
            </div>
            <div className='flex items-center justify-center mt-10'>
              <img 
                src="assets/testimonial/cab.png" 
                alt="" 
                className='w-[60px] h-[48px] mb-25 mr-5' 
              />
              <p className='text-[28px] font-medium text-center mx-5 text-[#100062]'>
                {testimonial.quote}
              </p>
              <img 
                src={testimonial.imageSrc} 
                alt="" 
                className='w-[60px] h-[48px] mt-25 ml-5' 
              />
            </div>
          </div>
        ))}
      </section>
    </div>
  );
}
